import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { callProductEditAPI } from '../../apis/ProductEditAPICalls';

function ProductEdit({ productCode }) {
    const [productName, setProductName] = useState('');
    const [imageCount, setImageCount] = useState('');
    const [productThumbAddr, setProductThumbAddr] = useState([]);
    const [images, setImages] = useState([]);
    const [refCategoryCode, setRefCategoryCode] = useState('');
    const [price, setPriceOption] = useState('sell');
    const [productDesc, setProductDesc] = useState('');
    const [wishPlaceTrade, setWishPlaceTrade] = useState('');

    const MAX_IMAGES = 5;

    const handleDeleteImage = (index) => {
        const newImages = [...images];
        newImages.splice(index, 1);
        setImages(newImages);
        updateImageCount(newImages);
    };

    const updateImageCount = (imageList) => {
        setImageCount(imageList.length);
    };

    const handlePriceOptionChange = (option) => {
        setPriceOption(option);
        setPrice(option === 'share' ? '' : price);
    };

    const handlePriceChange = (event) => {
        setPrice(event.target.value);
    };



    useEffect(() => {
        const fetchProductInfo = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/products`);
                console.log('API Response:', response.data);
                const fetchedProductName = response.data?.productName;
                setProductName(fetchedProductName);
            } catch(error) {
                console.error('상품 정보를 가져오는 도중 에러 발생: ', error);
            }
        };

        if (productCode) {
            fetchProductInfo();
        }
    }, [productCode]);

    const onChangeHandler = (e) => {
        // form state 업데이트 로직을 추가하십시오.
        const { name, value } = e.target;
        switch (name) {
            case 'productName':
                setProductName(value);
                break;
            case 'productDescription':
                setProductDesc(value);
                break;
            case 'refCategoryCode':
                setRefCategoryCode(value);
                break;
            case 'wishPlaceTrade':
                setWishPlaceTrade(value);
                break;
            // 나머지 form state 업데이트 로직 추가
            default:
                break;
        }
    };

    const handleProductUpdate = () => {
        const updatedFields = {
            productCode,
            productName,
            productThumbAddr,
            refCategoryCode,
            price,
            productDesc,
            wishPlaceTrade
        };

        callProductEditAPI({ productCode, updatedFields });
    };

    return (
        <>
            <div>
                <h3>상품수정</h3>
                <hr />
                <div>
                    <label htmlFor="product_name" id="product_name" className="font_all">
                        상품명 *
                    </label>
                    <input
                        type="text"
                        name="productName"
                        id="product_name"
                        className="input_box"
                        placeholder="제목을 입력해 주세요"
                        required
                        value={productName}
                        onChange={onChangeHandler}
                    />
                    <br />
                    <div className="count_flex">
                        <label htmlFor="image_upload" className="font_all">
                            이미지 첨부*
                        </label>
                        <div className="image-count">{`(${imageCount}/${MAX_IMAGES})`}</div>
                        <div className="image-preview-container">
                            <label className="custom-file-upload">
                                <input
                                    type="file"
                                    className="custom-file-input"
                                    name="image_upload[]"
                                    id="image_upload"
                                    multiple
                                    required
                                    onChange={handleImageUpload}
                                />
                                +
                            </label>
                            <div>
                                <select
                                    name="ref_category_code"
                                    id="ref_category_code"
                                    className="input_box"
                                    value={refCategoryCode}
                                    onChange={(e) => setRefCategoryCode(e.target.value)}
                                    required
                                >
                                    <option value="" disabled>카테고리를 선택하세요</option>
                                    {categoryOptions.map((category) => (
                                        <option key={category.code} value={category.code}>{category.label}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="image-preview-container">
                            {images.map((image, index) => (
                                <div key={index} className="image-preview">
                                    <img
                                        className="image-item"
                                        src={URL.createObjectURL(image)}
                                        alt="Uploaded Image"
                                    />
                                    <button
                                        className="delete-image"
                                        onClick={() => handleDeleteImage(index)}
                                    >
                                        X
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
                <hr />
                <div className="pricing">
                    <div className="sell_h1">
                        <label className="font_all">가격 설정 *</label>
                        <div className="btn_two">
                            <label className="custom-radio">
                                <input
                                    type="radio"
                                    name="price_option"
                                    value="sell"
                                    required
                                    checked={priceOption === 'sell'} // 추가된 부분
                                    onChange={() => handlePriceOptionChange('sell')}
                                />
                                판매하기
                            </label>
                            <label className="custom-radio">
                                <input
                                    type="radio"
                                    name="price_option"
                                    value="share"
                                    required
                                    checked={priceOption === 'share'} // 추가된 부분
                                    onChange={() => handlePriceOptionChange('share')}
                                />
                                나눔하기
                            </label>
                        </div>
                    </div>
                    <br />
                    <input
                        type="number"
                        name="price"
                        id="price"
                        className="input_box"
                        placeholder={priceOption === 'share' ? '나눔입니다' : '가격을 입력하세요'}
                        disabled={priceOption === 'share'}
                        value={price}
                        onChange={handlePriceChange}
                    />
                    <span id="priceInfo" style={{ display: priceOption === 'share' ? 'inline' : 'none' }}>

                    </span>
                </div>
                <div className="explanation">
                    <label htmlFor="product_description" className="font_all">
                        상품 설명*
                    </label>
                    <textarea
                        name="product_description"
                        id="product_description"
                        placeholder="구매시기, 브랜드/모델명, 제품의 상태 (사용감, 하자 유무) 등을 입력해 주세요. 서로가 믿고 거래할 수 있도록, 자세한 정보와 다양한 각도의 상품 사진을 올려주세요.
            * 안전하고 건전한 거래 환경을 위해 과학기술정보통신부, 한국인터넷진흥원과 오일장(주)가 함께 합니다."
                        onChange={handleDescriptionChange}
                    ></textarea>
                    <br />
                    <p>*부적합한 게시글은 사전에 통보 없이 삭제 될 수 있음을 알려드립니다.</p>
                    <hr />
                </div>
                <div className="location">
                    <label htmlFor="product_description" className="font_all">
                        거래희망 장소*
                    </label>
                    <textarea
                        name="wishPlaceTrade"
                        id="wish"
                        className="input_box"
                        placeholder="위치 작성"
                        onChange={handleWish}
                    ></textarea>
                    <hr />
                </div>
                <div className="btn_send">
                    <div className="btn_all">
                        <button onClick={handleFormSubmit}>등록</button>
                        <button onClick={handleCancel}>취소</button>
                    </div>
                </div>
            </div>
        </>
    );
}
export default ProductEdit;
