import React, { useState } from 'react';
import axios from 'axios';
import { callProductEditAPI } from '../../apis/ProductEditAPICalls';


function ProductEdit( {productId }) {

    const ProductEdit = ({ productId }) => {

        const [productName, setProductName] = useState('');
        const [productThumbAddr, setProductThumbAddr] = useState([]);
        const [refCategoryCode, setRefCategoryCode] = useState('');
        const [price, setPriceOption] = useState('sell');
        const [productDesc, setProductDesc] = useState('');
        const [wishPlaceTrade, setWishPlaceTrade] = useState('');

        const handleProductUpdate = () => {
            const updatedFields = {
                productName,
                productThumbAddr,
                refCategoryCode,
                price,
                productDesc,
                wishPlaceTrade
            };

            callProductEditAPI({ productId, updatedFields });
        };
    }


    return (
        <>
            <div>
                <h3>상품 수정</h3>
                <hr />
                <label htmlFor="product_name" id="product_name" className="font_all">
                    상품명 *
                </label>
                <input
                    type="text"
                    name="productName"
                    id="product_name"
                    className="input_box"
                    required
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                />
            </div>
        </>
    );
};

export default ProductEdit;
