import axios from 'axios';

export const callProductEditAPI = async ({ productCode, updatedFields }) => {
    console.log('[ProductEditAPICalls] Product Update API Call');

    const requestURL = `http://localhost:8000/products`;

    try {
        const response = await axios.put(requestURL, updatedFields, {
            headers: {
                'Authorization': 'Bearer ' + window.localStorage.getItem('accessToken'),
                'Content-Type': 'application/json'
            }
        });

        if (response.status >= 200 && response.status < 300) {
            const result = response.data;
            console.log('[ProductEditAPICalls] callProductUpdateAPI RESULT:', result);
        } else {
            throw new Error('상품 수정에 실패했습니다.');
        }
    } catch (error) {
        console.error('[ProductEditAPICalls] 상품 수정 API 호출 중 에러:', error);
    }
};
