export const callProductEditAPI = ({ productCode, updatedFields }) => {
    console.log('[ProductEditAPICalls] product Update API Call');

    const requestURL = `http://localhost:8000/products/${productCode}`;

    const productDTO = {
        refUserCode: form.refUserCode,
        productName: form.productName,
        productThumbAddr: form.productThumbAddr,
        refCategoryCode: form.refCategoryCode,
        productPrice: form.productPrice,
        productDesc: form.productDesc || '',
        wishPlaceTrade: form.wishPlaceTrade || '',
      };
        console.log(productDTO);

    // const formData = new FormData();

    if (updatedFields.productCode) {
        formData.append('productCode', updatedFields.productCode);
    }

    console.log('Updated Fields:', updatedFields);

    return async (dispatch, getState) => {
        try {
            const response = await fetch(requestURL, {
                method: 'PUT',
                headers: {
                    'Authorization': 'Bearer ' + window.localStorage.getItem('accessToken'),
                },
                body: formData,
            });

            if (!response.ok) {
                throw new Error('상품 수정에 실패했습니다.');
            }

            const result = await response.json();
            console.log('[ProductEditAPICalls] callProductUpdateAPI RESULT:', result);
        } catch (error) {
            console.error('[ProductEditAPICalls] 상품 수정 API 호출 중 에러:', error);
        }
    }
}
